export default {
  props: ['task'],
  data() {
    return {
      date: null
    };
  },
  mounted() {
    if (this.task.dueDate) {
      this.date = this.task.dueDate;
    }
  },
  methods: {
    saveTask() {
      let payload = {
        id: this.task.id,
        dueDate: this.date
      };
      this.$store.dispatch('updateTaskDueDate', payload);
      this.$emit('close');
    }
  }
};
